namespace Heiflow.Models.Atmosphere
{
    public interface ILongWaveRadiation
    {
        double ReflectionRate { get; set; }
        double Emissivity(double temperature);

        double LongWaveRadiationFlux(double airTemperature, double waterTemperature, double cloudCove);
    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
    public class LongWaveRadiation:Radiation, ILongWaveRadiation
    {
        public LongWaveRadiation()
        {
            //Name = "Long Wave Radiation model";
            //Descriptions = "Long Wave Radiation model";
            //ID = "AC111";
        }

        public double ReflectionRate { get; set; }

        public double EmissivityContant { get; set; }


        public virtual double Emissivity(double temperature)
        {
            return 0;
        }

        /// <summary>
        /// air temperature is used as defualt value
        /// </summary>
        /// <param name="airTemperature"></param>
        /// <param name="waterTemperature">not used</param>
        /// <param name="cloudCover">notused</param>
        /// <returns></returns>
        public virtual double LongWaveRadiationFlux(double airTemperature, double waterTemperature, double cloudCover)
        {
            return Math.Pow(airTemperature + KelvinConstant, 4);
        }
    }
}
